$("#createAppointement input[type='datetime-local']").flatpickr({
    enableTime: true,
    dateFormat: "m/d/Y h:i K",
    defaultDate: false
});


$("#callenderAppointement input[type='datetime-local']").flatpickr({
    enableTime: false,
    dateFormat: "m/d/Y h:i K",
    defaultDate: false
});










